mapper.insert:
insert into favourite (
    houseId,
    userId
)
VALUES (
           #{houseId},
           #{email}
       )

mapper.update:
update house
set
    isAvailable =#{isAvailable}
where houseId =#{houseId}

mapper.select:
select houseId, houseImage, houseVenue, houseDescription, ownerId, districtId, isAvailable
from house
where districtId = #{districtId}

select houseId, houseImage, houseVenue, houseDescription, ownerId, districtId, isAvailable
from house
where houseId = #{houseId}
